import { useState } from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Platform, KeyboardAvoidingView } from 'react-native';
import ButtonSubmit from '../components/Shared/ButtonSubmit';

const RegistrationScreen = ({navigation}) => {
    const [isShowKeyboard, setIsShowKeyboard] = useState(false);
    console.log(isShowKeyboard)

    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === "ios" ? "padding" : "height"}
        >
        <View style= {styles.register}>
        <View style={{...styles.registerBox, marginBottom: isShowKeyboard ? 100 : 0}}>
            <View style={styles.photoBox}>
                <View style={styles.photoPlace}></View>
                <TextInput 
                    style={styles.photoAdd} 
                    type="file"
                    accept="image/png, image/jpeg"
                    placeholder="+"
                    placeholderTextColor="#FF6C00"
                    multiple
                    readonly
                />
            </View>

            <Text style={styles.boxTitle}>Registration</Text>

            <View style={styles.form}>
                <View style={styles.inputBackground}>
                    <TextInput 
                        style={styles.inputForm} 
                        placeholder={'Login'} 
                        maxLength={40}
                        placeholderTextColor={"#BDBDBD"}
                        onFocus={() => setIsShowKeyboard(true)}
                    />
                </View>
                <View style={styles.inputBackground}>
                    <TextInput 
                        style={styles.inputForm} 
                        placeholder={'Email'} 
                        maxLength={40}
                        placeholderTextColor={"#BDBDBD"}
                        onFocus={() => setIsShowKeyboard(true)}
                    />
                </View>
                <View style={styles.inputBackground}>
                    <TextInput 
                        style={styles.inputForm} 
                        placeholder={'Password'} 
                        maxLength={40} 
                        secureTextEntry={true}
                        placeholderTextColor={"#BDBDBD"}
                        onFocus={() => setIsShowKeyboard(true)}
                    />
                </View>
                <ButtonSubmit text="Register"></ButtonSubmit>
            </View>
            
            <TouchableOpacity
                style={styles.navLoginBtn}
                onPress={() =>
                    navigation.navigate('Login', {name: 'login'})
                }
            >
                <Text style={styles.navLoginText}>Already have an account? Enter</Text>
            </TouchableOpacity>
        </View>
        </View>
        </KeyboardAvoidingView>
    );
}

const styles = StyleSheet.create({
    register: {
        backgroundColor: "#F6F6F6",
        height: 650,
    },
    registerBox: {
        marginBottom: -100,
        display: "flex",
        justifyContent: 'flex-end',
        height: 549,
        backgroundColor: "#FFFFFF",
        borderTopLeftRadius: 25,
        borderTopRightRadius: 25,
    },
    boxTitle: {
        marginBottom: 33,
        textAlign: "center",
        fontSize: 30,
    },
    photoBox: {
        marginBottom: 30,
        display: "flex",
        flexDirection: "row",
        justifyContent: "center",
    },
    photoPlace: {
        width: 120,
        height: 120,
        backgroundColor: "#F6F6F6",
        borderRadius: 16,
    },
    photoAdd:{
        marginLeft: -15,
        marginTop: 81,
        width: 25,
        height: 25,
        backgroundColor: "#FFFFFF",
        borderRadius: 100,
        borderWidth: 1,
        borderColor: "#FF6C00",
        textAlign: "center",
        fontSize: 21,
    },
    form: {
        marginBottom: 16,
    },
    inputBackground: {
        marginBottom: 16,
        marginHorizontal: 16,
        borderWidth: 1,
        borderColor: "#E8E8E8",
        height: 50,
        borderRadius: 8,
        backgroundColor: "#F6F6F6",
    },
    inputForm: {
        marginHorizontal: 16,
        height: 50,
        fontSize: 16,
        textAlign: 'left',
    },
    navLoginBox:{
        display: "flex",
        flexDirection: "row",
        justifyContent: "center",
    },
    navLoginText: {
        color: "#1B4371",
        fontSize: 16,
    },
    navLoginBtn: {
        alignItems: "center",
        marginBottom: 78,
    }
});

export default RegistrationScreen;
